import React, {useState, useEffect} from 'react';
import classes from '../../CSS/Main.module.css'
const Game = () =>{
    const P1 = [[0,2,0,9,0,0,5,0,0,9,0,0,1,0,7,0,0,6,0,
        0,4,3,0,0,6,0,0,2,0,8,7,1,9,0,0,0,7,4,0,0,0,5,0,
        0,8,3,0,0,0,6,0,0,0,0,0,1,0,5,0,0,3,5,0,8,6,9,0,0,
        4,2,9,1,0,3,0,0,8,3,1,2,5]]
   let pushpuzzle = (
        <ul className={classes.Mainboard}>
            {P1[0].map((val,index)=>{
                let row_col = null;
                if(index%9 == 0){
                    row_col = "Leftcol"
                }
                else if((index +1) %9 == 0){
                    row_col = "Rightcol"
                }
                else if(index < 9){
                    row_col = "Toprow"
                }
                else if(index > 72){
                    row_col = "Botrow"
                }
                return(
                    <li className={classes["cell"+index]+" "+classes[row_col]}>{index}</li>
                )
            })}
        </ul>
    )
    
    return(
        <div >
            {pushpuzzle}
        </div>
    )
};
export default Game;
