import React ,{useState} from 'react';
import classes from '../../CSS/Main.module.css';
const Progress = props => {
    let opac = 0;
    if (props.complete > 0){
        opac = 0.5
    }
    else if (props.complete >100){
        setTimeout(()=>{
            opac = 0;
        },1000)
    }
    const [style,setStyle] = React.useState({});
    setTimeout(()=>{
        const newStyle = {
            opacity: `${opac}`,
            width: `${props.complete}%`
        }
        setStyle(newStyle);
    }, 500);
    return(
    <div className={classes.progress}>
        <div className={classes.progresscomplete} style={style}>
            
        </div>
    </div>
    )
}
