import React, { useState, useEffect } from 'react';
import fire from './fire';
import LoginWindow from './LoginWindow';
import { Redirect } from 'react-router-dom';

const LoginHandling = () => {
    const [user, setUser] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [userNameError, setuserNameError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const clearInputs = () => {
        setUsername('');
        setPassword('');
    }
    const clearErrors = () => {
        setuserNameError('');
        setPasswordError('');
    }
    const loginHandler = () => {
        clearErrors('');
        fire
            .auth()
            .signInWithEmailAndPassword(username+"@sudokureact.com", password)
            .catch(err => {
                switch (err.code) {
                        case "auth/invalid":
                        case "auth/user-disabled":
                        case "auth/user-not-found":
                        setuserNameError(err.message);
                        break;
                    case "auth/wrong-password":
                        setPasswordError(err.message);
                        break;
                }
            })
    }

    const signupHandler = () => {
        clearErrors();
        fire
            .auth()
            .createUserWithEmailAndPassword(username+"@sudokureact.com", password).then(user=>{
                console.log("test")
                return fire.firestore().collection('users').doc(user.email).set({highScore:0});
            })
            .catch(err => {
                switch (err.code) {
                    case "auth/email-already-in-use":
                    case "auth/invalid-user":
                        setuserNameError(err.message);
                        console.log(userNameError)
                        break;
                    case "auth/weak-password":
                        setPasswordError(err.message);
                        break;                       
                }
         
                
                
            })
            
        
        
    }
    const logOutHandler = () => {
        fire.auth().signOut();
    }
    const authListener = () => {
        fire.auth().onAuthStateChanged(user => {
            if (user) {
                clearInputs();
                setUser(user);
            }
            else {
                setUser('');
            }
        })
    }
    useEffect(() => {
        authListener();
    }, [])
    return (
        <div>
            {user ?(
                <Redirect to="/"/>
                ):(
                <LoginWindow
                    username={username}
                    setUsername={setUsername}
                    password={password}
                    setPassword={setPassword}
                    loginHandler={loginHandler}
                    signupHandler={signupHandler}
                    userNameError={userNameError}
                    passwordError={passwordError}
                />)
            }
        </div>
    )
}
export default LoginHandling
