import React, {useState, useEffect} from 'react';
import classes from '../../CSS/Main.module.css'

const Timer = (props) =>{
    let timearr=props.children.split(':')
    let min = timearr[0]
    let sec = timearr[1]
    if(sec<10){
        timearr[1] = sec++
        sec = "0"+timearr[1]
    }
    console.log(sec)
    let timer = min +":"+ sec
    return(
        <div>{timer}</div>
    )
}

export default Timer;
