import React, {useState, useEffect} from 'react';
import classes from '../../CSS/Main.module.css'

const Timer = (props) =>{
    
    const [time,settime] = useState({
        sec: '00',
        min: '00'
    })
    let timer = time.min+':'+time.sec
    setTimeout(countUp,1000)
    const countUp = () =>{
        let min = time.min
        let sec = parseInt(time.sec)
        if(sec<10){
            settime({
                sec:'0'+(sec+ 1).toString(),
                min: min
            })
        }
        timer = time.min+':'+time.sec
        console.log("test")
    }
    return(
        <div>{timer}</div>
    )
}

export default Timer;
