import React, { useState, useEffect } from 'react';
import LoginWindow from './LoginWindow';
import { Redirect } from 'react-router-dom';
import axios from 'axios';
import { getFromStorage, setFromStorage } from '../utils/localstorage';
import { Verify, Signup, Login } from '../utils/requests';
const LoginHandling = () => {
    const [user, setUser] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [passwordConf, setPasswordConf] = useState('');
    const [passwordConfError, setPasswordConfError] = useState('');
    const [receiveError, setReceiveError] = useState('');
    let userJSON = {
        username: username,
        password: password,
        score: 0
    }

    const clearInputs = () => {
        setUsername('');
        setPassword('');
    }
    const clearErrors = () => {
        setReceiveError('');
    }
    const loginHandler = () => {
        clearErrors('');
        
    }

    const signupHandler = () => {
        clearErrors();
        if(password == passwordConf){
            Signup(userJSON)
            .then( resolve => { 
                console.log(resolve)
                // if (res.data.succes) {
                //     console.log("Signed up")
                // } else {
                //     setReceiveError(res.data.message)
                // }
            });
            } else {
                setPasswordConfError('Please make sure both passwords match')
            }
        
    }
    const logOutHandler = () => {

    }
    const authListener = () => {
        
    }
    useEffect(() => {
        const obj = getFromStorage('sudoku_react');
        if (obj && obj.token) {
            const { token } = obj;
        if(Verify(token)) {
            setUser(token)
        } else {
            setUser('')
        }
    }
    }, [])
    return (
        <>
            {user ?(
                <Redirect to="/"/>
                ):(
                <LoginWindow
                    username={username}
                    setUsername={setUsername}
                    password={password}
                    setPassword={setPassword}
                    setPasswordConf={setPasswordConf}
                    loginHandler={loginHandler}
                    signupHandler={signupHandler}
                    receiveError={receiveError}
                    passwordConfError={passwordConfError}
                />)
            }
        </>
    )
}
export default LoginHandling
