import React, {useState,useEffect} from 'react';
import classes from '../../../CSS/GameStyle.module.css'
import padContext from '../../Context/Pad-context'
const LogicContainers = props =>{
    
    let pushpuzzle = (
        <ul className={classes.GameComp} >
            {props.Data.map((val,index)=>{
                let status = null;
                if(val==null){
                    status = "dynamic"
                }
                else{
                    status = "static"
                }
            return(
                <padContext.Consumer>
                {(context) => {
                    return(
                    <li key={index} status={status} id={index} onClick={()=>{context.PadHandler(event, status)}} >{val}</li>)}}
                </padContext.Consumer>
                )
            })}
        </ul>
    )
    return(
        <div>
            {pushpuzzle}
            
        </div>
    )

}
export default LogicContainers;
