import React, {useState, useEffect} from 'react';
import classes from '../../CSS/Main.module.css'
import Logo from '../../Assets/Navbar/Logo.png'
import Progress from './LoadingElem';
import {Link} from 'react-router-dom';

const Navbar = props => {

    const [loadState, setloadState] = useState({
        loadprog: 0,
        opacity: 0
        
    });

    
    const LoadPage = () =>{
        setTimeout(() =>{
            setloadState({
                loadprog: 70,
                opacity: 1
            })
        },100)
        setTimeout(() =>{
            setloadState({
                loadprog: 100,
            })
        },300)
        setTimeout(() =>{
            setloadState({
                loadprog: 101,
                opacity: 0
            })
        },1500)
        setTimeout(() =>{
            setloadState({
                loadprog: 0,
            })
        },1700)
         
    }
    useEffect(()=>{
        LoadPage()
    },[])

    return(
       <main className={classes.Navbar}>
            <nav className={classes.NavbarBody}>
                <span><img className={classes.Logo} src={Logo} alt="Logo"></img></span>
                <ul className={classes.NavButtons}>
                    <li className={classes.NavButton}>
                            <span onClick={LoadPage}><Link to="/Game">PLAY</Link></span></li>
                    <li className={classes.NavButton}>
                        <span onClick={LoadPage}><Link to="/Rankings">RANKINGS</Link></span></li>
                </ul>
                <ul className={classes.NavButtonLogin}>
                    <li className={classes.NavButton}>
                        <Link onClick={LoadPage} to="/Login">LOGIN</Link></li>
                </ul>
            </nav>
            <Progress opacity={loadState.opacity} complete={loadState.loadprog} loadbar={loadState.loadprog}/>
        </main> 
    )
};
export default Navbar;
