        let min = parseInt(timearr[0])
        let sec = parseInt(timearr[1])
        if(sec<9){
            timearr[1] ='0'+(sec+ 1).toString()

        }
        else if (sec == 59&& min<9){
            timearr[1]= '00'
            timearr[0] = '0'+(min+ 1).toString()
           
        }
        else if(sec == 59&& min>8){
            timearr[1]= '00'
            timearr[0] =(min+ 1).toString()   
        }
        else{
            timearr[1] =(sec+ 1).toString()
        }
        settimer({timer: (timearr[0]+':'+timearr[1])})
        
        console.log(timer)
    }
    setTimeout(countUp,1000)
    console.log(timer)
    return(
        <div>{timer.timer}</div>
    )
}

export default Timer;
