import React, { useState } from 'react';

import classes from '../../CSS/Main.module.css';
import Game from './Game';
import Timer from '../Timer/Timer';


const GameContainer = () =>{
    const[LoadTemp,setLoadTemp]=useState({opacity:0,margin:1})

    setTimeout(()=>{
        setLoadTemp({opacity:1,margin:1})
    },500)
    const [gameState,setgameState]= useState({gameState:false})
    const gameHandler =()=>{
        let tempstate = gameState.gameState
        setgameState({gameState:!tempstate})
     }
    return(
        <div className={classes.App} style={{opacity:`${LoadTemp.opacity}`,marginTop:`${LoadTemp.margin}em`}}>
            <Timer gameState={gameState.gameState}/>
            <Game 
                gameState={gameState.gameState}
                gameHandler={gameHandler}/>
        </div>
    )
}
export default GameContainer;
