import React, {useState, useEffect, useRef} from 'react';
import classes from '../../CSS/GameStyle.module.css';
import padContext from '../Context/Pad-context';


const Timer = (props) =>{
    const [gamestate,setgamestate]=useState({gameState:null})
    const [timersec,settimersec] =useState('00');
    const [timermin,settimermin] =useState('00');
    let interval = useRef();
    const countUp = () =>{
        interval = setTimeout(() => {
        let min = parseInt(timermin)
        let sec = parseInt(timersec)
        if(sec<9){
            settimersec ('0'+(sec+ 1).toString())

        }
        else if (sec == 59&& min<9){
            settimersec('00')
            settimermin ('0'+(min+ 1).toString())
        
        }
        else if(sec == 59&& min>8){
            settimersec('00')
            settimermin ((min+ 1).toString())   
        }
        else{
            settimersec ((sec+ 1).toString())
        }
        
    }, 1000);
    };
    useEffect(()=>{
        if(gamestate.gameState){
            console.log("test"+props.startGame)
            countUp();
            return()=>{
                clearTimeout(interval)
            }
        }
    })
    const triggerTimer =()=>{
        settimersec('test')

        let tempstate = gamestate.gameState
        setgamestate({
            gameState: !tempstate
        })   
        console.log("Triggered timer")
    };
    
    return(
        <padContext.Provider value={{gameState:gamestate.gameState ,triggerTimer: triggerTimer}}>
        <div className={classes.Timer}>{timermin+":"+timersec}</div>
        <button onClick={triggerTimer}>TEST</button>
        </padContext.Provider>
    )
}

export default Timer;
