import React, {} from 'react';
import classes from '../../../CSS/GameStyle.module.css';
import padInputContext from '../../Context/Pad-Input-context'
const InputPad = (props) =>{
    const style = {
        top: props.Ycoords-69,
        left: props.Xcoords-64,
        opacity: 1
    }
    const keyPads=[]

    for(var num = 1; num<10 ;num++){
        console.log(num)
    keyPads.push(
        <padInputContext.Consumer>
        { 
        (context, num)=>(
            
            <li onClick={context.InputHandler.bind(num)}>{num}</li>
            )
        }
        </padInputContext.Consumer>
        )
        
    }


    return(
        <div className={classes.PadContainer} style={style} >
            <ul className={classes.Padstyle}  >
                {keyPads}
            </ul>
        </div>
    )
}
export default InputPad;
