            else{
                timearr[1] =(sec+ 1).toString()
            }
            timerDiv.current.innerHTML = timearr[0]+':'+timearr[1]
            
            console.log("hello")
    }
  setInterval(countUp,1000)
    
    // useEffect(()=>{
