    const [user, setUser] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [passwordConf, setPasswordConf] = useState('');
    const [passwordConfError, setPasswordConfError] = useState('');
    const [userNameError, setuserNameError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    let userJSON = {
        username: username,
        password: password,
        score: 0
    }

    const clearInputs = () => {
        setUsername('');
        setPassword('');
    }
    const clearErrors = () => {
        setuserNameError('');
        setPasswordError('');
    }
    const loginHandler = () => {
        clearErrors('');
        
    }

    const signupHandler = () => {
        clearErrors();
        if(password == passwordConf){
            axios.post('http://localhost:5000/users/signup',userJSON)
            .then(res => console.log(res))
            .catch(err => console.log(err));
            }
        else{
            setPasswordConfError('Please make sure both passwords match')
        }
    }
    const logOutHandler = () => {

    }
    const authListener = () => {
        
    }
    useEffect(() => {
       
    }, [])
    return (
        <>
            {user ?(
                <Redirect to="/"/>
                ):(
                <LoginWindow
                    username={username}
                    setUsername={setUsername}
                    password={password}
                    setPassword={setPassword}
                    setPasswordConf={setPasswordConf}
                    loginHandler={loginHandler}
                    signupHandler={signupHandler}
                    userNameError={userNameError}
                    passwordError={passwordError}
                    passwordConfError={passwordConfError}
                />)
            }
        </>
    )
}
export default LoginHandling
