import React, { Component } from 'react';
import Navbar from './components/Navbar/Navbar';
import classes from './CSS/Main.module.css';
import Game from './components/Game/Game';
import Solve from './components/Buttons/Solve';
import Finish from'./components/Buttons/Finish';
import Timer from './components/Timer/Timer';
class App extends Component {
  state = {
    gameState:false
  }
  gameHandler =()=>{
    let tempstate = this.state.gameState
    this.setState({gameState:!tempstate})
 }
  render() {
    let StartFin = <Solve onClick={this.gameHandler} gameState={this.state.gameState}></Solve>
    if(this.state.gameState){
      StartFin= <Finish/>
    }
    return (
      <div className={classes.App}>
        <Navbar></Navbar>
        <Timer gameState={this.state.gameState}/>
        <Game gameState={this.state.gameState}></Game>
        {StartFin}
      </div>
    )
  }
}

export default App;
