import React, {useState, useEffect, useRef} from 'react';
import classes from '../../CSS/Main.module.css'

const Timer = () =>{
    // let timearr = ['00','00']
    const [timersec,settimersec] =useState('00');
    const [timermin,settimermin] =useState('00');
    let interval = useRef();
    const countUp = () =>{
        interval = setInterval(() => {
            
       
        let min = parseInt(timermin)
        let sec = parseInt(timersec)
        if(sec<9){
            settimersec ('0'+(sec+ 1).toString())

        }
        else if (sec == 59&& min<9){
            settimersec('00')
            settimermin ('0'+(min+ 1).toString())
        
        }
        else if(sec == 59&& min>8){
            settimersec('00')
            settimermin ((min+ 1).toString())   
        }
        else{
            settimersec ((sec+ 1).toString())
        }
        console.log(timermin+":"+timersec)
    }, 1000);
    }
   
    useEffect(()=>{
        countUp();
        console.log("hello")
         return()=>{
             clearInterval(interval.current)
         }
     });
    

    
   
    return(
        <div className="TimerClass">{timermin+":"+timersec}</div>
    )
}

export default Timer;
