import React, {useState,useEffect} from 'react';
import classes from '../../../CSS/GameStyle.module.css'
import padContext from '../../Context/Pad-context'
import padInputContext from '../../Context/Pad-Input-context'
const LogicContainers = props =>{
    
    let pushpuzzle = (
        <ul className={classes.GameComp} >
            
            {props.Data.map((val,index)=>{
                let status = null;
                let inputValue = val
                if(val==null){
                    status = "dynamic"
                }
                else{
                    status = "static"
                }
                const InputHandler=(padinput)=>{
                    console.log("Ran")
                    inputValue = padinput
                }
            
                
            return(
                <li key={index} status={status} id={index} onClick={(event)=>{props.PadHandler(event,status,props.Secotor)}} >{inputValue}</li>
                )
            })}
            
        </ul>
    )
    return(
        <div>
            {pushpuzzle}
            
        </div>
    )

}
export default LogicContainers;
