import React, {useState, useEffect, useRef} from 'react';
import classes from '../../CSS/Main.module.css'

const Timer = () =>{
    let timearr = ['00','00']
    let timer = '00:00'
    const [timerstate,settimerstate] =useState();

    const countUp = () =>{
        interval = setInterval(() => {
            
       
        let min = parseInt(timearr[0])
        let sec = parseInt(timearr[1])
        if(sec<9){
            timearr[1] ='0'+(sec+ 1).toString()

        }
        else if (sec == 59&& min<9){
            timearr[1]= '00'
            timearr[0] = '0'+(min+ 1).toString()
        
        }
        else if(sec == 59&& min>8){
            timearr[1]= '00'
            timearr[0] =(min+ 1).toString()   
        }
        else{
            timearr[1] =(sec+ 1).toString()
        }
        timer= timearr[0]+':'+timearr[1]
        
        console.log(timer)
    }, 1000);
    }
    
     
 let interval = useRef(null);
    useEffect(()=>{
        countUp();
        settimerstate({
            timer: timer
        })
         return()=>{
             clearInterval(interval.current)
         }
     },timer);
   
    return(
        <div className="TimerClass">{timer}</div>
    )
}

export default Timer;
