import React, {useState} from 'react';
import classes from '../../../CSS/GameStyle.module.css'
import InputPad from './Input';
const LogicContainers = props =>{
    
    let inputmenu = null;
    
   
    if(props.showPad){
        inputmenu = (
        <InputPad Ycoords={props.Ycoords} Xcoords={props.Xcoords}/>
        );
    }
    
    let pushpuzzle = (
        <ul className={classes.GameComp}onMouseDown={props.Onclick} onMouseUp={props.Onclick}>
            {props.Data.map((val,index)=>{
                
                return(
                <li key={index} id={index} >{val}</li>
                )
            })}
        </ul>
    )
    return(
        <div>
            {pushpuzzle}
            {inputmenu}
        </div>
    )

}
export default LogicContainers;
