import React, {useState, useEffect} from 'react';
import classes from '../../CSS/GameStyle.module.css';
import LogicContainers from './GridComponents/LogicContainers';
import GridOverlay  from './GridComponents/GridOverlay';
import InputPad from './GridComponents/Input';
import padContext from '../Context/Pad-context';
import Solve from '../Buttons/Solve';
import Timer from '../Timer/Timer';
const Game = () =>{
    const [Padstate,setPadstate] = useState({
        showPad:false,
        Xcoords: null,
        Ycoords:null,
        cellindex: null
    })
    const ClosePad = () =>{
        setPadstate({
            showPad:false
        })
    }
    const PadHandler =(event, stat, Sector,index)=>{
        if(Padstate.showPad||stat=="static"){
            setPadstate({
                showPad:false
            });
        }
        else{    
            setPadstate({
                showPad:true,
                Xcoords: event.clientX,
                Ycoords: event.clientY,
                cellindex: index,
                Sector: Sector
            })
        }
    }
    const InputHandler=(padinput)=>{
        let Tempstate = ActivePuzzle
        console.log(padinput.toString())
        Tempstate[Padstate.Sector][Padstate.cellindex]= padinput.toString()

        SetPuzzle(Tempstate)
        setPadstate({
            showPad:false
        })
 
    }
    let inputmenu = null;
    if(Padstate.showPad){
        inputmenu = (
        <InputPad InputHandler={InputHandler}Ycoords={Padstate.Ycoords} Xcoords={Padstate.Xcoords}/>
        );
    }
    const P1 = [[0,2,0,9,0,0,5,0,0,9,0,0,1,0,7,0,0,6,0,
        0,4,3,0,0,6,0,0,2,0,8,7,1,9,0,0,0,7,4,0,0,0,5,0,
        0,8,3,0,0,0,6,0,0,0,0,0,1,0,5,0,0,3,5,0,8,6,9,0,0,
        4,2,9,1,0,3,0,0,8,3,1,2,5]]
        
    


    const  [ActivePuzzle,SetPuzzle] = useState({
        R1C1:[0,2,0,9,0,0,5,0,0],R1C2:[0,2,0,9,0,null,5,0,0],R1C3:[0,2,0,9,0,0,null,0,0],
        R2C1:[0,2,0,9,0,0,null,0,0],R2C2:[0,2,0,9,0,0,5,0,0],R2C3:[0,2,0,null,0,0,5,0,0],
        R3C1:[0,2,0,9,0,0,5,0,null],R3C2:[0,2,0,9,0,0,5,0,0],R3C3:[0,null,0,9,0,0,5,0,0],});
    const [gameState,setgameState] = useState({
        startGame:false
    })
    const gameHandler =()=>{
       let tempstate = gameState.startGame
       setgameState({
           startGame: !tempstate
       })
    }
    let   GameComponents = (
        <div className={classes.GameComp}>
            {Object.keys(ActivePuzzle).map((Sector,key)=>{
                return(
                    <LogicContainers start={gameState.startGame} {...Padstate} PadHandler={PadHandler}Data={ActivePuzzle[Sector]} Sector={Sector}key={key}/>
                )
            })}

        </div>
    )
  
    return(
        <div >
            
            <div className={classes.GameStyleContainer} >
                <Timer gameStart={gameState.startGame}/>
                <GridOverlay/>
                <padContext.Provider value={{showPad: Padstate.showPad, PadHandler:PadHandler}}>
                <div className={classes.GameContainer}>
                    {GameComponents}
                </div>
                </padContext.Provider>
            </div>
            {inputmenu}
            <Solve onClick={gameHandler}>START</Solve>
        </div>
    )
};
export default Game;
