import React, {useState, useEffect} from 'react';
import classes from '../../CSS/Main.module.css'

const Finish =(props) =>{
    const solvePuzzle = (Puzzle) => {
        let dupeCheck = []
        for (let n = 1; n < 10; n++) {                                          //Horizontal Check
            for (let i = 0; i < 9; i++) {
                for (let m = i + 1; m < 9; m++) {                      
                    if (Puzzle["R" + n][i] == Puzzle["R" + n][m]) {
                        // console.log(Puzzle["R" + n][i], Puzzle["R" + n][m])  
                        return console.log("HORIZONTAL")           
                    }
                }
            }
        }                                                                      //
        for (let i = 0; i < 9; i++) {                                          //Vertical Check     
            for (let n = 1; n < 9; n++) {
                for (let m = n + 1; m < 9; m++) {
                    if (Puzzle["R" + n][i] == Puzzle["R" + (m)][i]) {
                        // console.log(Puzzle["R" + n][i], Puzzle["R" + (m)][i])
                        return console.log("VERTICAL")
                    }
                }
            }
        }                                                                      //
        for (let t = 0; t < 7; t++) {                                                                                           //SubGrid Check
            for (let n = 1; n < 4; n++) {
                for (let p = 0; p < 3; p++) {
                    for (let i = 1; i < 4; i++) {
                        for (let m = 0; m < 3; m++) {
                            // console.log("Puzzle[R" + (n + t) + "][" + p + "]" + "==" + "Puzzle[R" + i + "][" + m + "]")
                            if (Puzzle["R" + (n + t)][p + t] == Puzzle["R" + (i + t)][m]) {
                                dupeCheck.push(Puzzle["R" + i][m])
                            }
                        }
                    }
                }
            }
        }
        if (dupeCheck.length !== 9) {
            return console.log(dupeCheck.length+"SUBGRID")
        }
        console.log("CORRECT")
    }
    useEffect(()=>{
        if(props.Puzzle == null){
        
        }
        else{
            console.log(props.Puzzle)
            solvePuzzle(props.Puzzle)
        }
    },[props.Puzzle])
    return(
        <div className={classes.PageBtns}>
            <button onClick={props.onClick} className={classes.Solvebtn}>FINISH</button>
        </div>
    )
}
export default Finish;
