import React, {useState, useEffect, useRef} from 'react';
import classes from '../../CSS/GameStyle.module.css';
import { checkPropTypes } from 'prop-types';

const Timer = (props) =>{
    const [timersec,settimersec] =useState('00');
    const [timermin,settimermin] =useState('00');
    let interval = useRef();
    const countUp = () =>{
        interval = setTimeout(() => {
        let min = parseInt(timermin)
        let sec = parseInt(timersec)
        if(sec<9){
            sec = ('0'+(sec+ 1).toString())

        }
        else if (sec == 59&& min<9){
            sec=('00')
            min= ('0'+(min+ 1).toString())
        
        }
        else if(sec == 59&& min>8){
            sec=('00')
            min= ((min+ 1).toString())   
        }
        else{
            sec= ((sec+ 1).toString())
        }
        settimersec(sec)
        settimermin(min)
    }, 1000);
    }
    
    useEffect(()=>{
        if(props.gameStart){
            countUp();
            return()=>{
                clearTimeout(interval)
            }
        }
     });
     
    return(
        <div className={classes.Timer}>{timermin+":"+timersec}</div>
    )
}

export default Timer;
