import React, {useState, useEffect} from 'react';
import classes from '../../CSS/Main.module.css'

const Timer = () =>{
    
    let timearr = ['00','00']
    let timer = timearr[0]+':'+timearr[1]
    
    const countUp = () =>{
        let min = timearr[0]
        let sec = parseInt(timearr[1])
        if(sec<9){
            timearr[1] ='0'+(sec+ 1).toString()

        }
        else if (sec == 60&& min<9){
            timearr[1] = '00'
           
        }
        else if(sec == 60&& min>8){
            timearr[0] =(min+ 1).toString()   
        }
        else{
            timearr[1] =(sec+ 1).toString()
        }
        timer = (timearr[0]+':'+timearr[1])
        console.log(timer)
    }
    setInterval(countUp,1000)
    console.log(timer)
    return(
        <div>{timer}</div>
    )
}

export default Timer;
