import axios from 'axios';
export function Login() {

}
export function Signup(userData, res) {
    const defres = {
        success:false,
        message: 'Error: Cannot reach signup server'
    }
    axios.post('http://localhost:5000/users/signup', userData)
    .then(res => {
        defres = res.data
    })
    res.send(defres)
}
export function Verify(token) {
    axios.post('http://localhost:5000/users/verify?token='+ token)
        .then(res => res.json())
        .then(json => {
            if(json.success) {
                return token
            } else {
                return false
            }
        })
}
