import React, { Component } from 'react';
import Navbar from './components/Navbar/Navbar';
import classes from './CSS/Main.module.css';
import Game from './components/Game/Game';
import Solve from './components/Buttons/Solve';
import Finish from'./components/Buttons/Finish';
import Timer from './components/Timer/Timer';
class App extends Component {
  state = {
    gameState:false,
    puzzleComplete:false,
    completeToCheck:null
  }
  gameHandler =()=>{
    let tempstate = this.state.gameState
    this.setState({gameState:!tempstate})
 }
 puzzleFinish =()=>{
  this.setState({puzzleComplete:true})
 }
 puzzleGrabber =(puzzle)=>{
   if(this.state.puzzleComplete){
    this.setState({completeToCheck:puzzle})
   }
 }
  render() {
    let StartFin = <Solve onClick={this.gameHandler} gameState={this.state.gameState}></Solve>
    if(this.state.gameState){
      StartFin= <Finish onClick={this.puzzleGrabber}Puzzle={this.state.completeToCheck}/>
    }
    return (
      <div className={classes.App}>
        <Navbar></Navbar>
        <Timer gameState={this.state.gameState}/>
        <Game gameState={this.state.gameState} puzzleSubmit={this.puzzleGrabber}></Game>
        {StartFin}
      </div>
    )
  }
}

export default App;
