import React from 'react';
import classes from '../../CSS/Main.module.css';
const Progress = props => {
    let completeCheck = 0;
    React.useEffect(() => {
        setTimeout( ()=>{
            if (props.complete == 100){
                props.complete = 101;
            }
        },200)
    },[])
    const [style,setStyle] = React.useState({});
    setTimeout(()=>{
        const newStyle = {
            opacity: `${completeCheck== 101 ? 0 : 1}`,
            width: `${props.complete}%`
        }
        setStyle(newStyle);
    }, 1000);
    return(
    <div className={classes.progress}>
        <div className={classes.progresscomplete} style={style}>
            
        </div>
    </div>
    )
}
